css.style:
flex-grow: 1;
flex-basis: 250px;
min-width: 112px;
background-position: center;
height: 250px;
background-color: grey;
background-size: cover;
display: flex;
cursor: pointer;
position: relative;

> div.label{
  	padding: 6px;
	margin-top: auto;
  	width: 100%;
  	color: white;
  	background-color: rgba(0, 0, 0, 0.45);
  	overflow: hidden;
  	text-overflow: ellipsis;
  	white-space: nowrap;
}

> div.float{
  position: absolute;
  left: calc(50% - 50px);
  top: calc(50% - 50px);

  > div.icon{
	  margin-bottom: -4px;
	  background-color: #FFA000;
	  height: 100px;
	  width: 100px;
	  display: inline-block;
	  border-radius: 2px;
	  position: relative;

	&:before{
	  background-color: rgba(255, 255, 255, 0.35);
	  content: "";
	  width: 72px;
	  position: absolute;
	  top: 0;
	  right: 0;
	  height: 12px;
	  border-bottom-left-radius: 3px;
	  box-shadow: inset 1px -1px 10px rgba(68, 68, 68, 0.49);
	}
  }
}
