css.style:
// Button Color
background-color: white;
cursor: pointer;
border: none;
padding: 3px;
margin-top: 6px;
white-space: nowrap;
text-align: left;

> span{
	line-height: 20px;
  	margin-bottom: 20px;
}

// File Icon
> div.icon{
  margin-bottom: -4px;
  background-color: #307cf1;
  height: 20px;
  width: 15px;
  display: inline-block;
  border-radius: 2px;
  position: relative;

  &:before{
  	content: "";
	border-width: 4px;
    border-style: solid;
    border-color: #fff #fff rgba(255,255,255,.35) rgba(255,255,255,.35);
	position: absolute;
    top: 0;
    right: 0;
    width: 0;
    height: 0;
    border-bottom-left-radius: 2px;
  }
}

js.initialize-after:
